// page init
$(function(){
	initBlocksHeight();
	initInputs();
	window.setTimeout(initCarousel, 0);
});


// markup layout fix
function initBlocksHeight(){
	// basic examples
	setSameHeight({
		holder: '.alert-block',
		elements: 'div.same-height',
		useMinHeight: false,
		flexible: true
	});
	
	// multirow examples
	setSameHeight({
		holder: 'ul.list',
		elements: 'strong.caption',
		flexible: true,
		multiLine: true
	});
	setSameHeight({
		holder: 'ul.list',
		elements: 'div.info-box',
		flexible: true,
		multiLine: true
	});
}

// clear inputs on focus
function initInputs() {
	PlaceholderInput.replaceByOptions({
		// filter options
		clearInputs: true,
		clearTextareas: true,
		clearPasswords: true,
		skipClass: 'default',
		
		// input options
		showPasswordBullets: false,
		wrapWithElement: false,
		showUntilTyping: false,
		getParentByClass: false,
		placeholderAttr: 'value'
	});
}

// scroll gallery init
function initCarousel() {
	jQuery('div.main-gallery').scrollGallery({
		mask: 'div.mask',
		slider: '>ul',
		slides: '>li',
		disableWhileAnimating: true,
		btnPrev: 'a.prev',
		btnNext: 'a.next',
		generatePagination: 'div.pagination',
		circularRotation: true,
		pauseOnHover: false,
		autoRotation: true,
		maskAutoSize: false,
		switchTime: 4000,
		animSpeed: 600,
		step: 1
	});
	
	
	jQuery('div.small-gallery').scrollGallery({
		mask: 'div.mask',
		slider: '>ul',
		slides: '>li',
		btnPrev: 'a.prev',
		btnNext: 'a.next',
		currentNumber: 'span.cur-num',
		totalNumber: 'span.all-num',
		disableWhileAnimating: true,
		circularRotation: true,
		pauseOnHover: false,
		autoRotation: false,
		maskAutoSize: false,
		switchTime: 2000,
		animSpeed: 600,
		step: 1
	});
}

